import React, {component, Component} from 'react'
import "bootstrap/dist/css/bootstrap.css"
// import Card from "bootstrap/dist/css/card"

class Catalog extends Component {
    state = {
        value: 1
    };
    increment = () =>{
        // this.state.value = this.state.value + 1;
        console.log("increment clicked")
        this.setState(this.state.value +=1);
    }

    render(){
        return(
            <div>
                <span className="m-1">{this.getcount()}</span>
                <button className="button mt-2" onClick={this.increment}> addition</button>
                <button className="button mt-1 m-1"> deletion</button>
            </div>
        );
    }

    getcount(){
        const {value: count} = this.state;
        return count;
    }
}
export default Catalog
