import React, {Component} from 'react'
import Innercomponents from "./Innercomponents"
import "bootstrap/dist/css/bootstrap.css"
// import Card from "bootstrap/dist/css/card"

class AutoComplete extends Component {
    state = {
        value: 1,
        inputText:'',
        autoComplete:["cars","bikes","coupe", "sedan", "wagon", "sportscar", "luxury car"],
        emptyvalue : "no values to display",
        carsClicked:false,
        valueclicked:'',
        filteredValues:[]
    };
    // increment = () =>{
    //     // this.state.value = this.state.value + 1;
    //     console.log("increment clicked")
    //     this.setState({value : this.state.value +=1});
    // };

    inputOnChange = e =>{
        const inputValue=e.target.value;
        const {autoComplete}=this.state;
        let stateObj={}
        this.setState({inputText:inputValue})
        if(inputValue==="") {
            stateObj.filteredValues=[] ;
            stateObj.valueclicked=""}
        else stateObj.filteredValues=autoComplete.filter(item=> item.toLowerCase().startsWith(inputValue.toLowerCase()))
        this.setState(stateObj)
    }
    render(){
        const {inputText,filteredValues,carsClicked}=this.state;
        return(
            <div>
                {/* <span className="m-1">{this.getcount()}</span>
                <button className="button mt-2" onClick={this.increment}> addition</button>
                <button className="button mt-1 m-1"> deletion</button> */}
                
                <div className="container">
                    <div className="card">
                        <div className="card-body">
                            <pre>
                            <label>Please enter vehicle type like car: </label>
                            <input type="text" name="v_type" onChange={(e)=>this.inputOnChange(e)} value={inputText}/>
                            </pre>
                        </div>
                    </div>
                
                <div className="card-body">
                {filteredValues.length>0 && 
                
                filteredValues.map((filteredValue,index)=>{
                return <div key={`${index}_filter`} className="card-body"> <pre><p key={`${index}_filter`} onClick={()=>{
                    this.setState({carsClicked:!carsClicked, valueclicked:filteredValue});
                    }}>
                 { filteredValue } </p> </pre> </div>
                })
                }
                {this.state.valueclicked !== "" && 
                <div><Innercomponents filteredValue={this.state.valueclicked}/></div>}
                </div>
                
                {filteredValues.length === 0 && inputText!== "" && <div>No results found</div>}          


            </div>
            </div>
        );
    }

    // valueclickedfun = (valueclicked) =>
    // {
    //     console.log(valueclicked + "clicked in function");
    //     // this.setState(this.state.valueclicked = valueclicked);
    //     // <Innercomponents/>
    //     console.log(valueclicked)
    //     if(valueclicked !== ""){
    //         return <div><Innercomponents filteredValue={valueclicked}/></div>            
    //     }
    //     else
    //     {
    //         return <Innercomponents filteredValue={valueclicked}/>
    //     }
        
    // }

    getcount(){
        const {value: count} = this.state;
        return count;
    }
}
export default AutoComplete
